from Zwierze import Zwierzefrom random import randintimport pygameclass Antylopa(Zwierze):    def __init__(self, x, y, swiat):        super().__init__()        self.sila = 4        self.inicjatywa = 4        self.polozenie_x = x        self.polozenie_y = y        self.swiat = swiat        self.kolor = (205, 92, 92)    def akcja(self):        while 1:            x_t = self.polozenie_x            y_t = self.polozenie_y            kierunek = randint(0, 1)            zwrot = randint(0, 1)            if kierunek == 0:                if zwrot == 0:                    x_t += 2                else:                    x_t -= 2            else:                if zwrot == 0:                    y_t += 2                else:                    y_t -= 2            if 0 <= x_t < self.swiat.getRozmiarPlanszy()[0] and 0 <= y_t < self.swiat.getRozmiarPlanszy()[1]:                if self.swiat.organizmy[x_t][y_t] is None:                    self.swiat.organizmy[x_t][y_t] = self                    self.swiat.organizmy[self.polozenie_x][self.polozenie_y] = None                    self.polozenie_x = x_t                    self.polozenie_y = y_t                    break                else:                    self.swiat.organizmy[x_t][y_t].kolizja(self, self.swiat.organizmy[x_t][y_t])                    break    def kolizja(self, atakujacy, obronca):        if isinstance(atakujacy, type(obronca)):            super(Antylopa, self).rozmnazanie(atakujacy, obronca)            return        a = randint(0, 1)        x = obronca.getPolozenieX()        y = obronca.getPolozenieY()        if a == 0:            super(Antylopa, self).kolizja(atakujacy, obronca)        else:            while True:                x_t = obronca.getPolozenieX()                y_t = obronca.getPolozenieY()                kierunek = randint(0, 1)                zwrot = randint(0, 1)                if kierunek == 0:                    if zwrot == 0:                        x_t += 1                    else:                        x_t -= 1                else:                    if zwrot == 0:                        y_t += 1                    else:                        y_t -= 1                if 0 <= x_t < self.swiat.getRozmiarPlanszy()[0] and 0 <= y_t < self.swiat.getRozmiarPlanszy()[1]\                        and obronca.swiat.organizmy[x_t][y_t] is None:                    obronca.swiat.organizmy[x_t][y_t] = self                    obronca.swiat.organizmy[self.polozenie_x][self.polozenie_y] = None                    obronca.setPolozenieX(x_t)                    obronca.setPolozenieY(y_t)                    break