from Roslina import Roslinaimport Cyberowcaclass BarszczSosnowskiego(Roslina):	def __init__(self, x, y, swiat):		super().__init__()		self.sila = 10		self.polozenie_x = x		self.polozenie_y = y		self.swiat = swiat		self.kolor = (128, 128, 0)	def akcja(self):		x = self.getPolozenieX()		y = self.getPolozenieY()		i = -1		while i < 2:			if not self.swiat.organizmy[x + 1][y + i] is None and not isinstance(self.swiat.organizmy[x + 1][y + i],																				 Cyberowca.Cyberowca):				self.swiat.organizmy[x + 1][y + i] = None			if not self.swiat.organizmy[x - 1][y - 1] is None and not isinstance(self.swiat.organizmy[x - 1][y - 1],																				 Cyberowca.Cyberowca):				self.swiat.organizmy[x - 1][y - i] = None			if not self.swiat.organizmy[x][y - 1] is None and not isinstance(self.swiat.organizmy[x][y - 1],																				 Cyberowca.Cyberowca):				self.swiat.organizmy[x][y - 1] = None			if not self.swiat.organizmy[x][y + 1] is None and not isinstance(self.swiat.organizmy[x][y + 1],																				 Cyberowca.Cyberowca):				self.swiat.organizmy[x][y + 1] = None			i += 1	def kolizja(self, atakujacy, obronca):		if not isinstance(atakujacy, Cyberowca.Cyberowca):			x = atakujacy.getPolozenieX()			y = atakujacy.getPolozenieY()			obronca.swiat.usunZKolejki(atakujacy.getPolozenieX(), atakujacy.getPolozenieY())			obronca.swiat.organizmy[x][y] = None		else:			atakujacy.kolizja(atakujacy, obronca)