from Zwierze import Zwierzefrom random import randintclass Lis(Zwierze):	def __init__(self, x, y, swiat):		super().__init__()		self.sila = 3		self.inicjatywa = 7		self.polozenie_x = x		self.polozenie_y = y		self.swiat = swiat		self.kolor = (255, 140, 0)	def akcja(self):		while 1:			x_t = self.polozenie_x			y_t = self.polozenie_y			kierunek = randint(0, 1)			zwrot = randint(0, 1)			if kierunek == 0:				if zwrot == 0:					x_t += 1				else:					x_t -= 1			else:				if zwrot == 0:					y_t += 1				else:					y_t -= 1			if 0 <= x_t < self.swiat.getRozmiarPlanszy()[0] and 0 <= y_t < self.swiat.getRozmiarPlanszy()[1]:				if self.swiat.organizmy[x_t][y_t] is None:					self.swiat.organizmy[x_t][y_t] = self					self.swiat.organizmy[self.polozenie_x][self.polozenie_y] = None					self.polozenie_x = x_t					self.polozenie_y = y_t					break				elif self.swiat.organizmy[x_t][y_t].getSila() <= self.getSila():					self.swiat.organizmy[x_t][y_t].kolizja(self, self.swiat.organizmy[x_t][y_t])					break