from Organizm import Organizmfrom random import randintclass Roslina(Organizm):	def __init__(self):		super(Roslina, self).__init__()		self.inicjatywa = 0		self.sila = 0	def kolizja(self, atakujacy, obronca):		x = atakujacy.getPolozenieX()		y = atakujacy.getPolozenieY()		x_obr = obronca.getPolozenieX()		y_obr = obronca.getPolozenieY()		if atakujacy.getSila() >= obronca.getSila():			atakujacy.swiat.usunZKolejki(x_obr, y_obr)			atakujacy.swiat.organizmy[x_obr][y_obr] = None			atakujacy.swiat.organizmy[x_obr][y_obr] = atakujacy			atakujacy.swiat.organizmy[x_obr][y_obr].setPolozenieX(x_obr)			atakujacy.swiat.organizmy[x_obr][y_obr].setPolozenieY(y_obr)			atakujacy.swiat.organizmy[x][y] = None			return		else:			atakujacy.swiat.usunZKolejki(x, y)	def akcja(self):		szansa = randint(0, 19)		if szansa == 0:			wolne_pola = []			super(Roslina, self).szukajWolnychPol(self, wolne_pola)			ilosc_pol = len(wolne_pola)			if ilosc_pol != 0:				index = randint(0, ilosc_pol - 1)				pole = wolne_pola[index]				self.swiat.dodajOrganizm(pole, type(self))